import React from 'react';
import {
  Alert,
  AlertTitle,
  Box,
  Button,
  Collapse,
  List,
  ListItem,
  ListItemText,
} from '@mui/material';
import { Error as ErrorIcon, Refresh as RefreshIcon } from '@mui/icons-material';

interface ErrorMessageProps {
  message: string;
  errors?: string[];
  severity?: 'error' | 'warning' | 'info';
  title?: string;
  onRetry?: () => void;
  retryText?: string;
  showDetails?: boolean;
  onClose?: () => void;
}

const ErrorMessage: React.FC<ErrorMessageProps> = ({
  message,
  errors = [],
  severity = 'error',
  title,
  onRetry,
  retryText = 'Reintentar',
  showDetails = false,
  onClose
}) => {
  const [detailsOpen, setDetailsOpen] = React.useState(showDetails && errors.length > 0);

  const handleToggleDetails = () => {
    setDetailsOpen(!detailsOpen);
  };

  return (
    <Box sx={{ width: '100%', mb: 2 }}>
      <Alert 
        severity={severity}
        icon={<ErrorIcon />}
        onClose={onClose}
        action={
          onRetry && (
            <Button
              color="inherit"
              size="small"
              onClick={onRetry}
              startIcon={<RefreshIcon />}
            >
              {retryText}
            </Button>
          )
        }
      >
        {title && <AlertTitle>{title}</AlertTitle>}
        {message}

        {errors.length > 0 && (
          <Box sx={{ mt: 1 }}>
            <Button
              size="small"
              onClick={handleToggleDetails}
              sx={{ p: 0, minWidth: 'auto', textTransform: 'none' }}
            >
              {detailsOpen ? 'Ocultar detalles' : 'Ver detalles'}
            </Button>

            <Collapse in={detailsOpen}>
              <List dense sx={{ mt: 1 }}>
                {errors.map((error, index) => (
                  <ListItem key={index} sx={{ py: 0.5, px: 0 }}>
                    <ListItemText 
                      primary={`• ${error}`}
                      slotProps={{
                        primary: {
                          variant: 'body2',
                          color: 'text.secondary'
                        }
                      }}
                    />
                  </ListItem>
                ))}
              </List>
            </Collapse>
          </Box>
        )}
      </Alert>
    </Box>
  );
};

export default ErrorMessage;
