import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Container,
  Typography,
} from '@mui/material';
import Header from '../components/Header';
import StudentList from '../components/StudentList';
import RegistrationModal from '../components/RegistrationModal';

const PublicPage: React.FC = () => {
  const [registrationModalOpen, setRegistrationModalOpen] = useState(false);
  const [refreshTrigger, setRefreshTrigger] = useState(0);
  const navigate = useNavigate();

  const theme = useTheme();

  const handleRegistrationClick = () => {
    setRegistrationModalOpen(true);
  };

  const handleAdminClick = () => {
    navigate('/login');
  };

  const handleRegistrationClose = () => {
    setRegistrationModalOpen(false);
  };

  const handleRegistrationSuccess = () => {
    setRegistrationModalOpen(false);
    // Trigger refresh of student list
    setRefreshTrigger(prev => prev + 1);
  };

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
      {/* Header */}
      <Header 
        onRegistrationClick={handleRegistrationClick}
        onAdminClick={handleAdminClick}
      />

      {/* Main Content */}
      <Box component="main" sx={{ flexGrow: 1, py: 4, backgroundColor: '#f5f5f5', minHeight: 'calc(100vh - 64px)' }}>
        <Container maxWidth="md">
          {/* Page Title */}
          <Box sx={{ mb: 4 }}>
            <Typography
              variant="h4"
              component="h1"
              sx={{
                fontWeight: 700,
                color: '#333333',
                mb: 1,
                fontSize: { xs: '1.75rem', md: '2.125rem' }
              }}
            >
              Participantes del Curso
            </Typography>
            <Typography
              variant="body1"
              sx={{
                color: '#666666',
                fontSize: '1rem',
                lineHeight: 1.5
              }}
            >
              Conoce a los estudiantes inscritos en este curso online.
            </Typography>
          </Box>

          {/* Student List Section */}
          <StudentList
            refreshTrigger={refreshTrigger}
            onRegistrationClick={handleRegistrationClick}
          />
        </Container>
      </Box>

      {/* Footer */}
      <Box
        component="footer"
        sx={{
          py: 3,
          px: 2,
          mt: 'auto',
          backgroundColor: '#f5f5f5',
          textAlign: 'center',
          borderTop: '1px solid #e0e0e0'
        }}
      >
        <Typography variant="body2" sx={{ color: '#999999', fontSize: '0.875rem' }}>
          Â© 2024 EduConnect. Todos los derechos reservados.
        </Typography>
      </Box>

      {/* Registration Modal */}
      <RegistrationModal
        open={registrationModalOpen}
        onClose={handleRegistrationClose}
        onSuccess={handleRegistrationSuccess}
      />
    </Box>
  );
};

export default PublicPage;
