import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Container,
  Typography,
  Paper,
  Divider,
  useTheme,
  useMediaQuery,
} from '@mui/material';
import Header from '../components/Header';
import StudentList from '../components/StudentList';
import RegistrationModal from '../components/RegistrationModal';

const PublicPage: React.FC = () => {
  const [registrationModalOpen, setRegistrationModalOpen] = useState(false);
  const [refreshTrigger, setRefreshTrigger] = useState(0);
  const navigate = useNavigate();

  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  const handleRegistrationClick = () => {
    setRegistrationModalOpen(true);
  };

  const handleAdminClick = () => {
    navigate('/login');
  };

  const handleRegistrationClose = () => {
    setRegistrationModalOpen(false);
  };

  const handleRegistrationSuccess = () => {
    setRegistrationModalOpen(false);
    // Trigger refresh of student list
    setRefreshTrigger(prev => prev + 1);
  };

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
      {/* Header */}
      <Header 
        onRegistrationClick={handleRegistrationClick}
        onAdminClick={handleAdminClick}
      />

      {/* Main Content */}
      <Box component="main" sx={{ flexGrow: 1, py: 3 }}>
        <Container maxWidth="lg">
          {/* Welcome Section */}
          <Paper 
            elevation={3}
            sx={{ 
              p: isMobile ? 3 : 4, 
              mb: 4,
              background: `linear-gradient(135deg, ${theme.palette.primary.main} 0%, ${theme.palette.primary.dark} 100%)`,
              color: 'white',
              textAlign: 'center'
            }}
          >
            <Typography 
              variant={isMobile ? 'h4' : 'h3'} 
              component="h1" 
              gutterBottom
              sx={{ fontWeight: 700 }}
            >
              Bienvenido a EduConnect
            </Typography>
            <Typography 
              variant={isMobile ? 'body1' : 'h6'} 
              sx={{ 
                opacity: 0.9,
                maxWidth: '600px',
                mx: 'auto',
                lineHeight: 1.6
              }}
            >
              Plataforma de gestión de estudiantes para cursos de desarrollo personal y espiritual
            </Typography>
          </Paper>

          {/* Student List Section */}
          <Paper elevation={2} sx={{ p: isMobile ? 2 : 3 }}>
            <StudentList refreshTrigger={refreshTrigger} />
          </Paper>
        </Container>
      </Box>

      {/* Footer */}
      <Box 
        component="footer" 
        sx={{ 
          py: 2, 
          px: 2, 
          mt: 'auto',
          backgroundColor: theme.palette.grey[100],
          textAlign: 'center'
        }}
      >
        <Typography variant="body2" color="text.secondary">
          © 2024 EduConnect - Sistema de Gestión de Estudiantes
        </Typography>
      </Box>

      {/* Registration Modal */}
      <RegistrationModal
        open={registrationModalOpen}
        onClose={handleRegistrationClose}
        onSuccess={handleRegistrationSuccess}
      />
    </Box>
  );
};

export default PublicPage;
