import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Container,
  Paper,
  Typography,
  TextField,
  Button,
  Alert,
  IconButton,
  useTheme,
  useMediaQuery,
} from '@mui/material';
import {
  ArrowBack as ArrowBackIcon,
  AdminPanelSettings as AdminIcon,
} from '@mui/icons-material';
import { useAuth } from '../contexts/AuthContext';
import { LoginCredentials } from '../services/api';

const LoginPage: React.FC = () => {
  const [credentials, setCredentials] = useState<LoginCredentials>({
    email: '',
    password: '',
  });
  const [errors, setErrors] = useState<{ [key: string]: string }>({});
  const [isLoading, setIsLoading] = useState(false);

  const navigate = useNavigate();
  const { login, isAuthenticated, error: authError, clearError } = useAuth();
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  // Redirect to admin if already authenticated
  useEffect(() => {
    if (isAuthenticated) {
      navigate('/admin');
    }
  }, [isAuthenticated, navigate]);

  // Clear auth error when component mounts
  useEffect(() => {
    clearError();
  }, [clearError]);

  const validateForm = (): boolean => {
    const newErrors: { [key: string]: string } = {};

    // Email validation
    if (!credentials.email.trim()) {
      newErrors.email = 'El correo electrónico es requerido';
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(credentials.email)) {
      newErrors.email = 'Ingresa un correo electrónico válido';
    }

    // Password validation
    if (!credentials.password.trim()) {
      newErrors.password = 'La contraseña es requerida';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleInputChange = (field: keyof LoginCredentials) => (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    setCredentials(prev => ({
      ...prev,
      [field]: event.target.value,
    }));

    // Clear field error when user starts typing
    if (errors[field]) {
      setErrors(prev => ({
        ...prev,
        [field]: '',
      }));
    }

    // Clear auth error when user starts typing
    if (authError) {
      clearError();
    }
  };

  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault();

    if (!validateForm()) {
      return;
    }

    setIsLoading(true);

    try {
      await login(credentials.email, credentials.password);
      // Navigation will be handled by useEffect when isAuthenticated changes
    } catch (error) {
      // Error is handled by AuthContext and displayed via authError
    } finally {
      setIsLoading(false);
    }
  };

  const handleBackToPublic = () => {
    navigate('/');
  };

  return (
    <Box sx={{ 
      display: 'flex', 
      flexDirection: 'column', 
      minHeight: '100vh',
      backgroundColor: theme.palette.grey[50]
    }}>
      {/* Header */}
      <Box 
        sx={{ 
          py: 2, 
          px: 2,
          backgroundColor: 'white',
          borderBottom: `1px solid ${theme.palette.divider}`,
          boxShadow: 1
        }}
      >
        <Container maxWidth="lg">
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
            <IconButton 
              onClick={handleBackToPublic}
              color="primary"
              size="small"
            >
              <ArrowBackIcon />
            </IconButton>
            <Typography variant="h6" color="primary" sx={{ fontWeight: 600 }}>
              EduConnect
            </Typography>
          </Box>
        </Container>
      </Box>

      {/* Main Content */}
      <Box 
        component="main" 
        sx={{ 
          flexGrow: 1, 
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          py: 4
        }}
      >
        <Container maxWidth="sm">
          <Paper 
            elevation={3}
            sx={{ 
              p: isMobile ? 3 : 4,
              borderRadius: 2,
              backgroundColor: 'white'
            }}
          >
            {/* Login Header */}
            <Box sx={{ textAlign: 'center', mb: 4 }}>
              <Box 
                sx={{ 
                  display: 'inline-flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  width: 64,
                  height: 64,
                  borderRadius: '50%',
                  backgroundColor: theme.palette.primary.main,
                  color: 'white',
                  mb: 2
                }}
              >
                <AdminIcon fontSize="large" />
              </Box>
              <Typography 
                variant={isMobile ? 'h5' : 'h4'} 
                component="h1" 
                gutterBottom
                sx={{ fontWeight: 700 }}
              >
                Panel de Administración
              </Typography>
              <Typography 
                variant="body1" 
                color="text.secondary"
                sx={{ maxWidth: '400px', mx: 'auto' }}
              >
                Ingresa tus credenciales para acceder al sistema de gestión de estudiantes
              </Typography>
            </Box>

            {/* Login Form */}
            <form onSubmit={handleSubmit}>
              <Box sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
                {authError && (
                  <Alert severity="error">
                    {authError}
                  </Alert>
                )}

                <TextField
                  label="Correo Electrónico"
                  type="email"
                  value={credentials.email}
                  onChange={handleInputChange('email')}
                  error={!!errors.email}
                  helperText={errors.email}
                  fullWidth
                  required
                  autoComplete="email"
                  disabled={isLoading}
                  variant="outlined"
                />

                <TextField
                  label="Contraseña"
                  type="password"
                  value={credentials.password}
                  onChange={handleInputChange('password')}
                  error={!!errors.password}
                  helperText={errors.password}
                  fullWidth
                  required
                  autoComplete="current-password"
                  disabled={isLoading}
                  variant="outlined"
                />

                <Box sx={{ display: 'flex', gap: 2, mt: 2 }}>
                  <Button 
                    onClick={handleBackToPublic} 
                    disabled={isLoading}
                    color="inherit"
                    variant="outlined"
                    fullWidth
                  >
                    Volver
                  </Button>
                  <Button 
                    type="submit" 
                    variant="contained" 
                    disabled={isLoading}
                    fullWidth
                    sx={{ minHeight: 48 }}
                  >
                    {isLoading ? 'Iniciando sesión...' : 'Iniciar Sesión'}
                  </Button>
                </Box>
              </Box>
            </form>
          </Paper>
        </Container>
      </Box>

      {/* Footer */}
      <Box 
        component="footer" 
        sx={{ 
          py: 2, 
          px: 2, 
          mt: 'auto',
          backgroundColor: 'white',
          borderTop: `1px solid ${theme.palette.divider}`,
          textAlign: 'center'
        }}
      >
        <Typography variant="body2" color="text.secondary">
          © 2024 EduConnect - Sistema de Gestión de Estudiantes
        </Typography>
      </Box>
    </Box>
  );
};

export default LoginPage;
