import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  Paper,
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Pagination,
} from '@mui/material';
import {
  PersonAdd as PersonAddIcon,
} from '@mui/icons-material';
import { studentApi, PublicStudent, ApiError } from '../services/api';
import LoadingSpinner from './LoadingSpinner';
import ErrorMessage from './ErrorMessage';
import { useNotification } from '../contexts/NotificationContext';

interface StudentListProps {
  refreshTrigger?: number; // Used to trigger refresh after new registration
  onRegistrationClick: () => void;
}

const StudentList: React.FC<StudentListProps> = ({ refreshTrigger = 0, onRegistrationClick }) => {
  const [students, setStudents] = useState<PublicStudent[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [retryCount, setRetryCount] = useState(0);
  const [currentPage, setCurrentPage] = useState(1);
  const studentsPerPage = 5;

  const { showError, showSuccess } = useNotification();

  // Calculate pagination
  const totalPages = Math.ceil(students.length / studentsPerPage);
  const startIndex = (currentPage - 1) * studentsPerPage;
  const endIndex = startIndex + studentsPerPage;
  const currentStudents = students.slice(startIndex, endIndex);

  const fetchStudents = async (showLoadingSpinner = true) => {
    try {
      if (showLoadingSpinner) {
        setLoading(true);
      }
      setError(null);
      const data = await studentApi.getPublicStudents();
      setStudents(data);

      // Show success message only on retry
      if (retryCount > 0) {
        showSuccess('Lista de estudiantes actualizada correctamente');
        setRetryCount(0);
      }
    } catch (err) {
      const apiError = err as ApiError;
      const errorMessage = apiError.message || 'Error al cargar la lista de estudiantes';
      setError(errorMessage);

      // Show notification for network errors
      if (apiError.status === 0 || errorMessage.toLowerCase().includes('conexión')) {
        showError(
          errorMessage,
          'Error de Conexión',
          {
            label: 'Reintentar',
            onClick: handleRetry
          }
        );
      }
    } finally {
      if (showLoadingSpinner) {
        setLoading(false);
      }
    }
  };

  useEffect(() => {
    fetchStudents();
  }, [refreshTrigger]);

  const handleRetry = () => {
    setRetryCount(prev => prev + 1);
    fetchStudents();
  };

  if (loading) {
    return (
      <LoadingSpinner 
        message="Cargando lista de estudiantes..." 
        size={50}
        timeout={8000}
        onTimeout={() => {
          showError(
            'La carga está tardando más de lo esperado. Verifica tu conexión a internet.',
            'Carga Lenta'
          );
        }}
      />
    );
  }

  if (error) {
    return (
      <ErrorMessage
        message={error}
        title="Error al cargar estudiantes"
        onRetry={handleRetry}
        retryText="Recargar lista"
        showRecoveryOptions={true}
        onGoHome={() => window.location.reload()}
        errorCode={retryCount > 0 ? `RETRY_${retryCount}` : undefined}
      />
    );
  }

  const handlePageChange = (_: React.ChangeEvent<unknown>, value: number) => {
    setCurrentPage(value);
  };

  if (students.length === 0) {
    return (
      <Box>
        {/* Total Students Card */}
        <Paper
          elevation={0}
          sx={{
            p: 4,
            mb: 4,
            backgroundColor: '#ffffff',
            border: '1px solid #e5e5e5',
            borderRadius: '16px',
            boxShadow: '0 2px 8px rgba(0, 0, 0, 0.08)',
            textAlign: 'center'
          }}
        >
          <Typography variant="body2" sx={{ mb: 1, color: '#666666', fontSize: '0.875rem' }}>
            Total de Estudiantes
          </Typography>
          <Typography variant="h3" sx={{ fontWeight: 700, color: '#1a1a1a' }}>
            0
          </Typography>
        </Paper>

        {/* Registration Button */}
        <Button
          variant="contained"
          startIcon={<PersonAddIcon />}
          onClick={onRegistrationClick}
          sx={{
            backgroundColor: '#4285f4',
            color: 'white',
            textTransform: 'none',
            borderRadius: '24px',
            fontSize: '0.875rem',
            fontWeight: 500,
            px: 4,
            py: 1.5,
            mb: 4,
            boxShadow: '0 2px 8px rgba(66, 133, 244, 0.3)',
            '&:hover': {
              backgroundColor: '#3367d6',
              boxShadow: '0 4px 12px rgba(66, 133, 244, 0.4)',
            },
          }}
        >
          Registrarte como Estudiante
        </Button>

        {/* Empty State */}
        <Paper
          elevation={0}
          sx={{
            p: 6,
            textAlign: 'center',
            backgroundColor: '#ffffff',
            border: '1px solid #e5e5e5',
            borderRadius: '16px',
            boxShadow: '0 2px 8px rgba(0, 0, 0, 0.08)'
          }}
        >
          <Typography variant="h6" sx={{ color: '#333333', mb: 1, fontWeight: 600 }}>
            No hay estudiantes registrados
          </Typography>
          <Typography variant="body2" sx={{ color: '#666666' }}>
            Sé el primero en registrarte a un curso
          </Typography>
        </Paper>
      </Box>
    );
  }

  return (
    <Box>
      {/* Total Students Card */}
      <Paper
        elevation={0}
        sx={{
          p: 4,
          mb: 4,
          backgroundColor: '#ffffff',
          border: '1px solid #e5e5e5',
          borderRadius: '16px',
          boxShadow: '0 2px 8px rgba(0, 0, 0, 0.08)',
          textAlign: 'center'
        }}
      >
        <Typography variant="body2" sx={{ mb: 1, color: '#666666', fontSize: '0.875rem' }}>
          Total de Estudiantes
        </Typography>
        <Typography variant="h3" sx={{ fontWeight: 700, color: '#1a1a1a' }}>
          {students.length}
        </Typography>
      </Paper>

      {/* Registration Button */}
      <Button
        variant="contained"
        startIcon={<PersonAddIcon />}
        onClick={onRegistrationClick}
        sx={{
          backgroundColor: '#4285f4',
          color: 'white',
          textTransform: 'none',
          borderRadius: '24px',
          fontSize: '0.875rem',
          fontWeight: 500,
          px: 4,
          py: 1.5,
          mb: 4,
          boxShadow: '0 2px 8px rgba(66, 133, 244, 0.3)',
          '&:hover': {
            backgroundColor: '#3367d6',
            boxShadow: '0 4px 12px rgba(66, 133, 244, 0.4)',
          },
        }}
      >
        Registrarte como Estudiante
      </Button>

      {/* Students List Title */}
      <Typography variant="h6" sx={{ mb: 3, fontWeight: 600, color: '#1a1a1a', fontSize: '1.125rem' }}>
        Lista de Participantes:
      </Typography>

      {/* Students Table */}
      <TableContainer
        component={Paper}
        elevation={0}
        sx={{
          mb: 4,
          backgroundColor: '#ffffff',
          border: '1px solid #e5e5e5',
          borderRadius: '16px',
          boxShadow: '0 2px 8px rgba(0, 0, 0, 0.08)',
          overflow: 'hidden',
          maxHeight: '400px', // Altura máxima para scroll
          '&::-webkit-scrollbar': {
            width: '8px',
          },
          '&::-webkit-scrollbar-track': {
            backgroundColor: '#f1f1f1',
            borderRadius: '4px',
          },
          '&::-webkit-scrollbar-thumb': {
            backgroundColor: '#c1c1c1',
            borderRadius: '4px',
            '&:hover': {
              backgroundColor: '#a8a8a8',
            },
          },
        }}
      >
        <Table stickyHeader>
          <TableHead>
            <TableRow>
              <TableCell
                sx={{
                  fontWeight: 600,
                  color: '#1a1a1a',
                  backgroundColor: '#f8f9fa',
                  borderBottom: '2px solid #e5e5e5',
                  fontSize: '0.875rem',
                  py: 2.5
                }}
              >
                Nombre
              </TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {currentStudents.map((student, index) => (
              <TableRow
                key={student.id}
                sx={{
                  '&:hover': {
                    backgroundColor: '#f8f9fa',
                    transition: 'background-color 0.2s ease'
                  },
                  borderBottom: index === currentStudents.length - 1 ? 'none' : '1px solid #f0f0f0'
                }}
              >
                <TableCell sx={{
                  py: 3,
                  color: '#333333',
                  fontSize: '0.9rem',
                  fontWeight: 500
                }}>
                  {student.nombre} {student.apellido}
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      {/* Pagination */}
      {totalPages > 1 && (
        <Box sx={{ display: 'flex', justifyContent: 'center', mb: 4 }}>
          <Pagination
            count={totalPages}
            page={currentPage}
            onChange={handlePageChange}
            color="primary"
            size="medium"
            sx={{
              '& .MuiPaginationItem-root': {
                color: '#666666',
                borderRadius: '12px',
                fontWeight: 500,
                minWidth: '40px',
                height: '40px',
                margin: '0 2px',
                border: '1px solid transparent',
                transition: 'all 0.2s ease',
                '&.Mui-selected': {
                  backgroundColor: '#4285f4',
                  color: 'white',
                  border: '1px solid #4285f4',
                  boxShadow: '0 2px 8px rgba(66, 133, 244, 0.3)',
                  '&:hover': {
                    backgroundColor: '#3367d6',
                    boxShadow: '0 4px 12px rgba(66, 133, 244, 0.4)',
                  },
                },
                '&:hover': {
                  backgroundColor: '#f8f9fa',
                  border: '1px solid #e5e5e5',
                },
              },
            }}
          />
        </Box>
      )}
    </Box>
  );
};

export default StudentList;
