import React, { useState, useEffect } from 'react';
import {
  Box,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Typography,
  IconButton,
  Chip,
  useTheme,
  useMediaQuery,
  Card,
  CardContent,
  Stack,
  Divider,
  Pagination,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
} from '@mui/material';
import {
  Edit as EditIcon,
  Person as PersonIcon,
  Phone as PhoneIcon,
  LocationOn as LocationIcon,
  School as SchoolIcon,
  Payment as PaymentIcon,
} from '@mui/icons-material';
import { studentApi, Student, ApiError } from '../services/api';
import LoadingSpinner from './LoadingSpinner';
import ErrorMessage from './ErrorMessage';
import { useNotification } from '../contexts/NotificationContext';

interface AdminStudentListProps {
  onEditStudent: (student: Student) => void;
  refreshTrigger?: number;
}

const AdminStudentList: React.FC<AdminStudentListProps> = ({
  onEditStudent,
  refreshTrigger = 0
}) => {
  const [students, setStudents] = useState<Student[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [retryCount, setRetryCount] = useState(0);

  // Pagination states
  const [currentPage, setCurrentPage] = useState(1);
  const [studentsPerPage, setStudentsPerPage] = useState(10);

  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));
  const { showError, showSuccess } = useNotification();

  // Calculate pagination
  const totalPages = Math.ceil(students.length / studentsPerPage);
  const startIndex = (currentPage - 1) * studentsPerPage;
  const endIndex = startIndex + studentsPerPage;
  const currentStudents = students.slice(startIndex, endIndex);

  const fetchStudents = async (showLoadingSpinner = true) => {
    try {
      if (showLoadingSpinner) {
        setLoading(true);
      }
      setError(null);
      const data = await studentApi.getAdminStudents();
      setStudents(data);

      // Show success message only on retry
      if (retryCount > 0) {
        showSuccess('Lista de estudiantes actualizada correctamente');
        setRetryCount(0);
      }
    } catch (err) {
      const apiError = err as ApiError;
      const errorMessage = apiError.message || 'Error al cargar la lista de estudiantes';
      setError(errorMessage);

      // Show notification for authentication errors
      if (apiError.status === 401) {
        showError(
          'Tu sesión ha expirado. Por favor, inicia sesión nuevamente.',
          'Sesión Expirada'
        );
      } else if (apiError.status === 0 || errorMessage.toLowerCase().includes('conexión')) {
        showError(
          errorMessage,
          'Error de Conexión',
          {
            label: 'Reintentar',
            onClick: handleRetry
          }
        );
      }
    } finally {
      if (showLoadingSpinner) {
        setLoading(false);
      }
    }
  };

  useEffect(() => {
    fetchStudents();
  }, [refreshTrigger]);

  const handleRetry = () => {
    setRetryCount(prev => prev + 1);
    fetchStudents();
  };

  const getPaymentStatusColor = (status: string) => {
    switch (status) {
      case 'completo':
        return 'success';
      case 'parcial':
        return 'warning';
      case 'pendiente':
      default:
        return 'error';
    }
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('es-CO', {
      style: 'currency',
      currency: 'COP',
      minimumFractionDigits: 0,
    }).format(amount);
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('es-CO', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
    });
  };

  if (loading) {
    return (
      <LoadingSpinner 
        message="Cargando estudiantes..." 
        size={50}
        timeout={8000}
        onTimeout={() => {
          showError(
            'La carga está tardando más de lo esperado. Verifica tu conexión a internet.',
            'Carga Lenta'
          );
        }}
      />
    );
  }

  if (error) {
    return (
      <ErrorMessage
        message={error}
        title="Error al cargar estudiantes"
        onRetry={handleRetry}
        retryText="Recargar lista"
        showRecoveryOptions={true}
        onGoHome={() => window.location.href = '/'}
        errorCode={retryCount > 0 ? `ADMIN_RETRY_${retryCount}` : undefined}
      />
    );
  }

  if (students.length === 0) {
    return (
      <Paper
        elevation={0}
        sx={{
          p: 4,
          textAlign: 'center',
          backgroundColor: '#ffffff',
          border: '1px solid #e5e5e5',
          borderRadius: '16px',
          boxShadow: '0 2px 8px rgba(0, 0, 0, 0.08)'
        }}
      >
        <PersonIcon
          sx={{
            fontSize: 64,
            color: '#cccccc',
            mb: 2
          }}
        />
        <Typography variant="h6" sx={{ color: '#1a1a1a', fontWeight: 600 }} gutterBottom>
          No hay estudiantes registrados
        </Typography>
        <Typography variant="body2" sx={{ color: '#666666' }}>
          Los estudiantes aparecerán aquí cuando se registren
        </Typography>
      </Paper>
    );
  }

  // Mobile card view
  if (isMobile) {
    return (
      <Box>
        {/* Total Students Card */}
        <Paper
          elevation={0}
          sx={{
            p: 4,
            mb: 4,
            backgroundColor: '#ffffff',
            border: '1px solid #e5e5e5',
            borderRadius: '16px',
            boxShadow: '0 2px 8px rgba(0, 0, 0, 0.08)',
            textAlign: 'center'
          }}
        >
          <Typography variant="body2" sx={{ mb: 1, color: '#666666', fontSize: '0.875rem' }}>
            Total de Estudiantes
          </Typography>
          <Typography variant="h3" sx={{ fontWeight: 700, color: '#1a1a1a' }}>
            {students.length}
          </Typography>
        </Paper>

        {/* Students List Title */}
        <Typography variant="h6" sx={{ mb: 3, fontWeight: 600, color: '#1a1a1a', fontSize: '1.125rem' }}>
          Lista de Estudiantes:
        </Typography>

        <Stack spacing={2}>
          {students.map((student) => (
            <Card
              key={student.id}
              elevation={0}
              sx={{
                backgroundColor: '#ffffff',
                border: '1px solid #e5e5e5',
                borderRadius: '12px',
                boxShadow: '0 2px 8px rgba(0, 0, 0, 0.08)',
                '&:hover': {
                  boxShadow: '0 4px 12px rgba(0, 0, 0, 0.12)',
                  transform: 'translateY(-1px)',
                },
                transition: 'all 0.2s ease-in-out',
              }}
            >
              <CardContent sx={{ p: 3 }}>
                <Box display="flex" justifyContent="space-between" alignItems="flex-start" mb={2}>
                  <Typography variant="h6" component="h3" sx={{ fontWeight: 600, color: '#1a1a1a' }}>
                    {student.nombre} {student.apellido}
                  </Typography>
                  <IconButton
                    onClick={() => onEditStudent(student)}
                    size="small"
                    sx={{
                      backgroundColor: '#f0f7ff',
                      color: '#4285f4',
                      borderRadius: '8px',
                      '&:hover': {
                        backgroundColor: '#e3f2fd',
                      },
                    }}
                  >
                    <EditIcon fontSize="small" />
                  </IconButton>
                </Box>

                <Stack spacing={2}>
                  <Box display="flex" alignItems="center" gap={1.5}>
                    <Box
                      sx={{
                        backgroundColor: '#f0f7ff',
                        borderRadius: '8px',
                        p: 0.75,
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center'
                      }}
                    >
                      <PhoneIcon sx={{ fontSize: '1rem', color: '#4285f4' }} />
                    </Box>
                    <Typography variant="body2" sx={{ color: '#1a1a1a', fontWeight: 500 }}>
                      {student.celular}
                    </Typography>
                  </Box>

                  <Box display="flex" alignItems="center" gap={1.5}>
                    <Box
                      sx={{
                        backgroundColor: '#f0f7ff',
                        borderRadius: '8px',
                        p: 0.75,
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center'
                      }}
                    >
                      <LocationIcon sx={{ fontSize: '1rem', color: '#4285f4' }} />
                    </Box>
                    <Typography variant="body2" sx={{ color: '#1a1a1a', fontWeight: 500 }}>
                      {student.ciudad}
                    </Typography>
                  </Box>

                  <Box display="flex" alignItems="center" gap={1.5}>
                    <Box
                      sx={{
                        backgroundColor: '#f0f7ff',
                        borderRadius: '8px',
                        p: 0.75,
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center'
                      }}
                    >
                      <SchoolIcon sx={{ fontSize: '1rem', color: '#4285f4' }} />
                    </Box>
                    <Typography variant="body2" sx={{ color: '#1a1a1a', fontWeight: 500 }}>
                      {student.curso}
                    </Typography>
                  </Box>

                  <Divider sx={{ my: 1, borderColor: '#e5e5e5' }} />

                  <Box display="flex" alignItems="center" justifyContent="space-between">
                    <Box display="flex" alignItems="center" gap={1.5}>
                      <Box
                        sx={{
                          backgroundColor: '#f0f7ff',
                          borderRadius: '8px',
                          p: 0.75,
                          display: 'flex',
                          alignItems: 'center',
                          justifyContent: 'center'
                        }}
                      >
                        <PaymentIcon sx={{ fontSize: '1rem', color: '#4285f4' }} />
                      </Box>
                      <Chip
                        label={student.estadoPago || 'pendiente'}
                        size="small"
                        color={getPaymentStatusColor(student.estadoPago || 'pendiente') as any}
                        sx={{
                          borderRadius: '8px',
                          fontWeight: 500,
                          fontSize: '0.75rem'
                        }}
                      />
                    </Box>
                    <Typography variant="body2" sx={{ color: '#1a1a1a', fontWeight: 600 }}>
                      {formatCurrency(student.cantidadPago || 0)}
                    </Typography>
                  </Box>

                  {student.fechaRegistro && (
                    <Typography variant="caption" sx={{ color: '#666666', fontSize: '0.75rem' }}>
                      Registrado: {formatDate(student.fechaRegistro)}
                    </Typography>
                  )}
                </Stack>
              </CardContent>
            </Card>
          ))}
        </Stack>
      </Box>
    );
  }

  // Desktop table view
  return (
    <Box>
      {/* Total Students Card */}
      <Paper
        elevation={0}
        sx={{
          p: 4,
          mb: 4,
          backgroundColor: '#ffffff',
          border: '1px solid #e5e5e5',
          borderRadius: '16px',
          boxShadow: '0 2px 8px rgba(0, 0, 0, 0.08)',
          textAlign: 'center'
        }}
      >
        <Typography variant="body2" sx={{ mb: 1, color: '#666666', fontSize: '0.875rem' }}>
          Total de Estudiantes
        </Typography>
        <Typography variant="h3" sx={{ fontWeight: 700, color: '#1a1a1a' }}>
          {students.length}
        </Typography>
      </Paper>

      {/* Students List Title */}
      <Typography variant="h6" sx={{ mb: 3, fontWeight: 600, color: '#1a1a1a', fontSize: '1.125rem' }}>
        Lista de Estudiantes:
      </Typography>

      <TableContainer
        component={Paper}
        elevation={0}
        sx={{
          mb: 4,
          backgroundColor: '#ffffff',
          border: '1px solid #e5e5e5',
          borderRadius: '16px',
          boxShadow: '0 2px 8px rgba(0, 0, 0, 0.08)',
          overflow: 'hidden',
          maxHeight: '500px',
          '&::-webkit-scrollbar': {
            width: '8px',
          },
          '&::-webkit-scrollbar-track': {
            backgroundColor: '#f1f1f1',
            borderRadius: '4px',
          },
          '&::-webkit-scrollbar-thumb': {
            backgroundColor: '#c1c1c1',
            borderRadius: '4px',
            '&:hover': {
              backgroundColor: '#a8a8a8',
            },
          },
        }}
      >
        <Table sx={{ minWidth: 650 }} stickyHeader>
          <TableHead>
            <TableRow>
              <TableCell sx={{
                fontWeight: 700,
                color: '#1a1a1a',
                backgroundColor: '#f8f9fa',
                borderBottom: '2px solid #e5e5e5',
                fontSize: '0.875rem'
              }}>
                Nombre
              </TableCell>
              <TableCell sx={{
                fontWeight: 700,
                color: '#1a1a1a',
                backgroundColor: '#f8f9fa',
                borderBottom: '2px solid #e5e5e5',
                fontSize: '0.875rem'
              }}>
                Apellido
              </TableCell>
              <TableCell sx={{
                fontWeight: 700,
                color: '#1a1a1a',
                backgroundColor: '#f8f9fa',
                borderBottom: '2px solid #e5e5e5',
                fontSize: '0.875rem'
              }}>
                Teléfono
              </TableCell>
              <TableCell sx={{
                fontWeight: 700,
                color: '#1a1a1a',
                backgroundColor: '#f8f9fa',
                borderBottom: '2px solid #e5e5e5',
                fontSize: '0.875rem'
              }}>
                Ciudad
              </TableCell>
              <TableCell sx={{
                fontWeight: 700,
                color: '#1a1a1a',
                backgroundColor: '#f8f9fa',
                borderBottom: '2px solid #e5e5e5',
                fontSize: '0.875rem'
              }}>
                Curso
              </TableCell>
              <TableCell sx={{
                fontWeight: 700,
                color: '#1a1a1a',
                backgroundColor: '#f8f9fa',
                borderBottom: '2px solid #e5e5e5',
                fontSize: '0.875rem'
              }}>
                Estado Pago
              </TableCell>
              <TableCell sx={{
                fontWeight: 700,
                color: '#1a1a1a',
                backgroundColor: '#f8f9fa',
                borderBottom: '2px solid #e5e5e5',
                fontSize: '0.875rem'
              }}>
                Cantidad
              </TableCell>
              <TableCell sx={{
                fontWeight: 700,
                color: '#1a1a1a',
                backgroundColor: '#f8f9fa',
                borderBottom: '2px solid #e5e5e5',
                fontSize: '0.875rem'
              }}>
                Registro
              </TableCell>
              <TableCell sx={{
                fontWeight: 700,
                color: '#1a1a1a',
                backgroundColor: '#f8f9fa',
                borderBottom: '2px solid #e5e5e5',
                fontSize: '0.875rem'
              }}>
                Acciones
              </TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {students.map((student, index) => (
              <TableRow
                key={student.id}
                sx={{
                  backgroundColor: index % 2 === 0 ? '#ffffff' : '#fafbfc',
                  '&:hover': {
                    backgroundColor: '#f0f7ff',
                    transform: 'scale(1.001)',
                  },
                  transition: 'all 0.2s ease-in-out',
                  borderBottom: '1px solid #e5e5e5',
                }}
              >
                <TableCell sx={{
                  color: '#1a1a1a',
                  fontWeight: 500,
                  fontSize: '0.875rem',
                  py: 2
                }}>
                  {student.nombre}
                </TableCell>
                <TableCell sx={{
                  color: '#1a1a1a',
                  fontWeight: 500,
                  fontSize: '0.875rem',
                  py: 2
                }}>
                  {student.apellido}
                </TableCell>
                <TableCell sx={{
                  color: '#666666',
                  fontSize: '0.875rem',
                  py: 2
                }}>
                  {student.celular}
                </TableCell>
                <TableCell sx={{
                  color: '#666666',
                  fontSize: '0.875rem',
                  py: 2
                }}>
                  {student.ciudad}
                </TableCell>
                <TableCell sx={{
                  color: '#666666',
                  fontSize: '0.875rem',
                  py: 2
                }}>
                  {student.curso}
                </TableCell>
                <TableCell sx={{ py: 2 }}>
                  <Chip
                    label={student.estadoPago || 'pendiente'}
                    size="small"
                    color={getPaymentStatusColor(student.estadoPago || 'pendiente') as any}
                    sx={{
                      borderRadius: '8px',
                      fontWeight: 500,
                      fontSize: '0.75rem'
                    }}
                  />
                </TableCell>
                <TableCell sx={{
                  color: '#1a1a1a',
                  fontWeight: 600,
                  fontSize: '0.875rem',
                  py: 2
                }}>
                  {formatCurrency(student.cantidadPago || 0)}
                </TableCell>
                <TableCell sx={{
                  color: '#666666',
                  fontSize: '0.75rem',
                  py: 2
                }}>
                  {student.fechaRegistro ? formatDate(student.fechaRegistro) : '-'}
                </TableCell>
                <TableCell sx={{ py: 2 }}>
                  <IconButton
                    onClick={() => onEditStudent(student)}
                    size="small"
                    sx={{
                      backgroundColor: '#f0f7ff',
                      color: '#4285f4',
                      borderRadius: '8px',
                      '&:hover': {
                        backgroundColor: '#e3f2fd',
                        transform: 'scale(1.1)',
                      },
                      transition: 'all 0.2s ease-in-out',
                    }}
                  >
                    <EditIcon fontSize="small" />
                  </IconButton>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Box>
  );
};

export default AdminStudentList;
