import React from 'react';
import {
  Box,
  Container,
  Typography,
  Button,
  useTheme,
  useMediaQuery,
} from '@mui/material';

interface HeaderProps {
  onRegistrationClick: () => void;
  onAdminClick: () => void;
}

const Header: React.FC<HeaderProps> = ({ onRegistrationClick, onAdminClick }) => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  return (
    <Box 
      component="header" 
      sx={{ 
        backgroundColor: 'white',
        borderBottom: '1px solid #e0e0e0',
        py: 2,
        px: 0
      }}
    >
      <Container maxWidth="lg">
        <Box sx={{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          alignItems: 'center',
          flexDirection: isMobile ? 'column' : 'row',
          gap: isMobile ? 2 : 0
        }}>
          {/* Logo */}
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
            <Box 
              sx={{ 
                width: 24, 
                height: 24, 
                backgroundColor: '#333',
                borderRadius: '2px'
              }} 
            />
            <Typography 
              variant="h6" 
              component="div" 
              sx={{ 
                fontWeight: 600,
                color: '#333',
                fontSize: '18px'
              }}
            >
              EduConnect
            </Typography>
          </Box>

          {/* Navegación */}
          <Box sx={{ 
            display: 'flex', 
            alignItems: 'center', 
            gap: isMobile ? 2 : 4 
          }}>
            <Button
              variant="text"
              sx={{
                color: '#333',
                fontSize: '14px',
                textTransform: 'none',
                fontWeight: 400,
                '&:hover': {
                  backgroundColor: 'transparent',
                  color: '#666'
                }
              }}
            >
              Inicio
            </Button>

            <Button
              variant="text"
              sx={{
                color: '#333',
                fontSize: '14px',
                textTransform: 'none',
                fontWeight: 400,
                '&:hover': {
                  backgroundColor: 'transparent',
                  color: '#666'
                }
              }}
            >
              Cursos
            </Button>

            <Button
              variant="text"
              sx={{
                color: '#333',
                fontSize: '14px',
                textTransform: 'none',
                fontWeight: 400,
                '&:hover': {
                  backgroundColor: 'transparent',
                  color: '#666'
                }
              }}
            >
              Comunidad
            </Button>

            <Button
              variant="text"
              sx={{
                color: '#333',
                fontSize: '14px',
                textTransform: 'none',
                fontWeight: 400,
                '&:hover': {
                  backgroundColor: 'transparent',
                  color: '#666'
                }
              }}
            >
              Recursos
            </Button>

            {/* Icono de notificación */}
            <Box 
              onClick={onRegistrationClick}
              sx={{ 
                width: 24, 
                height: 24, 
                display: 'flex', 
                alignItems: 'center', 
                justifyContent: 'center',
                cursor: 'pointer',
                '&:hover': {
                  backgroundColor: '#f0f0f0',
                  borderRadius: '4px'
                }
              }}
            >
              <Box sx={{ 
                width: 20, 
                height: 20, 
                border: '1px solid #ccc',
                borderRadius: '2px'
              }} />
            </Box>

            {/* Avatar de usuario */}
            <Box 
              onClick={onAdminClick}
              sx={{ 
                width: 32, 
                height: 32, 
                borderRadius: '50%', 
                backgroundColor: '#ff7043',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                cursor: 'pointer',
                '&:hover': {
                  backgroundColor: '#f4511e'
                }
              }}
            >
              <Typography sx={{ 
                color: 'white', 
                fontSize: '14px', 
                fontWeight: 600 
              }}>
                A
              </Typography>
            </Box>
          </Box>
        </Box>
      </Container>
    </Box>
  );
};

export default Header;
