import React, { useState, useEffect } from 'react';
import {
  Box,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Typography,
  IconButton,
  Chip,
  useTheme,
  useMediaQuery,
  Card,
  CardContent,
  Stack,
  Divider,
} from '@mui/material';
import {
  Edit as EditIcon,
  Person as PersonIcon,
  Phone as PhoneIcon,
  LocationOn as LocationIcon,
  School as SchoolIcon,
  Payment as PaymentIcon,
} from '@mui/icons-material';
import { studentApi, Student, ApiError } from '../services/api';
import LoadingSpinner from './LoadingSpinner';
import ErrorMessage from './ErrorMessage';
import { useNotification } from '../contexts/NotificationContext';

interface AdminStudentListProps {
  onEditStudent: (student: Student) => void;
  refreshTrigger?: number;
}

const AdminStudentList: React.FC<AdminStudentListProps> = ({ 
  onEditStudent, 
  refreshTrigger = 0 
}) => {
  const [students, setStudents] = useState<Student[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [retryCount, setRetryCount] = useState(0);

  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));
  const { showError, showSuccess } = useNotification();

  const fetchStudents = async () => {
    try {
      setLoading(true);
      setError(null);
      const data = await studentApi.getAdminStudents();
      setStudents(data);
    } catch (err) {
      const apiError = err as ApiError;
      setError(apiError.message || 'Error al cargar la lista de estudiantes');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchStudents();
  }, [refreshTrigger]);

  const handleRetry = () => {
    fetchStudents();
  };

  const getPaymentStatusColor = (status: string) => {
    switch (status) {
      case 'completo':
        return 'success';
      case 'parcial':
        return 'warning';
      case 'pendiente':
      default:
        return 'error';
    }
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('es-CO', {
      style: 'currency',
      currency: 'COP',
      minimumFractionDigits: 0,
    }).format(amount);
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('es-CO', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
    });
  };

  if (loading) {
    return (
      <LoadingSpinner 
        message="Cargando estudiantes..." 
        size={50}
      />
    );
  }

  if (error) {
    return (
      <ErrorMessage
        message={error}
        title="Error al cargar estudiantes"
        onRetry={handleRetry}
        retryText="Recargar lista"
      />
    );
  }

  if (students.length === 0) {
    return (
      <Paper 
        elevation={1} 
        sx={{ 
          p: 4, 
          textAlign: 'center',
          backgroundColor: theme.palette.grey[50]
        }}
      >
        <PersonIcon 
          sx={{ 
            fontSize: 64, 
            color: theme.palette.grey[400],
            mb: 2 
          }} 
        />
        <Typography variant="h6" color="text.secondary" gutterBottom>
          No hay estudiantes registrados
        </Typography>
        <Typography variant="body2" color="text.secondary">
          Los estudiantes aparecerán aquí cuando se registren
        </Typography>
      </Paper>
    );
  }

  // Mobile card view
  if (isMobile) {
    return (
      <Box>
        <Box sx={{ mb: 3, textAlign: 'center' }}>
          <Typography variant="h5" component="h2" gutterBottom>
            Gestión de Estudiantes
          </Typography>
          <Typography variant="body2" color="text.secondary">
            {students.length} {students.length === 1 ? 'estudiante registrado' : 'estudiantes registrados'}
          </Typography>
        </Box>

        <Stack spacing={2}>
          {students.map((student) => (
            <Card key={student.id} elevation={2}>
              <CardContent>
                <Box display="flex" justifyContent="space-between" alignItems="flex-start" mb={2}>
                  <Typography variant="h6" component="h3" sx={{ fontWeight: 600 }}>
                    {student.nombre} {student.apellido}
                  </Typography>
                  <IconButton
                    color="primary"
                    onClick={() => onEditStudent(student)}
                    size="small"
                  >
                    <EditIcon />
                  </IconButton>
                </Box>

                <Stack spacing={1.5}>
                  <Box display="flex" alignItems="center" gap={1}>
                    <PhoneIcon fontSize="small" color="action" />
                    <Typography variant="body2">{student.celular}</Typography>
                  </Box>

                  <Box display="flex" alignItems="center" gap={1}>
                    <LocationIcon fontSize="small" color="action" />
                    <Typography variant="body2">{student.ciudad}</Typography>
                  </Box>

                  <Box display="flex" alignItems="center" gap={1}>
                    <SchoolIcon fontSize="small" color="action" />
                    <Typography variant="body2">{student.curso}</Typography>
                  </Box>

                  <Divider />

                  <Box display="flex" alignItems="center" gap={1}>
                    <PaymentIcon fontSize="small" color="action" />
                    <Chip
                      label={student.estadoPago || 'pendiente'}
                      size="small"
                      color={getPaymentStatusColor(student.estadoPago || 'pendiente') as any}
                      variant="outlined"
                    />
                    <Typography variant="body2" color="text.secondary">
                      {formatCurrency(student.cantidadPago || 0)}
                    </Typography>
                  </Box>

                  {student.fechaRegistro && (
                    <Typography variant="caption" color="text.secondary">
                      Registrado: {formatDate(student.fechaRegistro)}
                    </Typography>
                  )}
                </Stack>
              </CardContent>
            </Card>
          ))}
        </Stack>
      </Box>
    );
  }

  // Desktop table view
  return (
    <Box>
      <Box sx={{ mb: 3, textAlign: 'center' }}>
        <Typography variant="h5" component="h2" gutterBottom>
          Gestión de Estudiantes
        </Typography>
        <Typography variant="body2" color="text.secondary">
          {students.length} {students.length === 1 ? 'estudiante registrado' : 'estudiantes registrados'}
        </Typography>
      </Box>

      <TableContainer component={Paper} elevation={2}>
        <Table sx={{ minWidth: 650 }}>
          <TableHead>
            <TableRow sx={{ backgroundColor: theme.palette.grey[50] }}>
              <TableCell sx={{ fontWeight: 600 }}>Nombre</TableCell>
              <TableCell sx={{ fontWeight: 600 }}>Apellido</TableCell>
              <TableCell sx={{ fontWeight: 600 }}>Teléfono</TableCell>
              <TableCell sx={{ fontWeight: 600 }}>Ciudad</TableCell>
              <TableCell sx={{ fontWeight: 600 }}>Curso</TableCell>
              <TableCell sx={{ fontWeight: 600 }}>Estado Pago</TableCell>
              <TableCell sx={{ fontWeight: 600 }}>Cantidad</TableCell>
              <TableCell sx={{ fontWeight: 600 }}>Registro</TableCell>
              <TableCell sx={{ fontWeight: 600 }}>Acciones</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {students.map((student) => (
              <TableRow 
                key={student.id}
                sx={{ 
                  '&:hover': { 
                    backgroundColor: theme.palette.action.hover 
                  } 
                }}
              >
                <TableCell>{student.nombre}</TableCell>
                <TableCell>{student.apellido}</TableCell>
                <TableCell>{student.celular}</TableCell>
                <TableCell>{student.ciudad}</TableCell>
                <TableCell>{student.curso}</TableCell>
                <TableCell>
                  <Chip
                    label={student.estadoPago || 'pendiente'}
                    size="small"
                    color={getPaymentStatusColor(student.estadoPago || 'pendiente') as any}
                    variant="outlined"
                  />
                </TableCell>
                <TableCell>{formatCurrency(student.cantidadPago || 0)}</TableCell>
                <TableCell>
                  {student.fechaRegistro ? formatDate(student.fechaRegistro) : '-'}
                </TableCell>
                <TableCell>
                  <IconButton
                    color="primary"
                    onClick={() => onEditStudent(student)}
                    size="small"
                  >
                    <EditIcon />
                  </IconButton>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Box>
  );
};

export default AdminStudentList;
