import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Container,
  Paper,
  Typography,
  TextField,
  Button,
  Alert,
  IconButton,
  useTheme,
  useMediaQuery,
  CircularProgress,
} from '@mui/material';
import {
  ArrowBack as ArrowBackIcon,
  AdminPanelSettings as AdminIcon,
  Login as LoginIcon,
} from '@mui/icons-material';
import { useAuth } from '../contexts/AuthContext';
import { LoginCredentials } from '../services/api';

const LoginPage: React.FC = () => {
  const [credentials, setCredentials] = useState<LoginCredentials>({
    email: '',
    password: '',
  });
  const [errors, setErrors] = useState<{ [key: string]: string }>({});
  const [isLoading, setIsLoading] = useState(false);

  const navigate = useNavigate();
  const { login, isAuthenticated, error: authError, clearError } = useAuth();
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  // Redirect to admin if already authenticated
  useEffect(() => {
    if (isAuthenticated) {
      navigate('/admin');
    }
  }, [isAuthenticated, navigate]);

  // Clear auth error when component mounts
  useEffect(() => {
    clearError();
  }, [clearError]);

  const validateForm = (): boolean => {
    const newErrors: { [key: string]: string } = {};

    // Email validation
    if (!credentials.email.trim()) {
      newErrors.email = 'El correo electr칩nico es requerido';
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(credentials.email)) {
      newErrors.email = 'Ingresa un correo electr칩nico v치lido';
    }

    // Password validation
    if (!credentials.password.trim()) {
      newErrors.password = 'La contrase침a es requerida';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleInputChange = (field: keyof LoginCredentials) => (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    setCredentials(prev => ({
      ...prev,
      [field]: event.target.value,
    }));

    // Clear field error when user starts typing
    if (errors[field]) {
      setErrors(prev => ({
        ...prev,
        [field]: '',
      }));
    }

    // Clear auth error when user starts typing
    if (authError) {
      clearError();
    }
  };

  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault();

    if (!validateForm()) {
      return;
    }

    setIsLoading(true);

    try {
      await login(credentials.email, credentials.password);
      // Navigation will be handled by useEffect when isAuthenticated changes
    } catch (error) {
      // Error is handled by AuthContext and displayed via authError
    } finally {
      setIsLoading(false);
    }
  };

  const handleBackToPublic = () => {
    navigate('/');
  };

  return (
    <Box sx={{
      display: 'flex',
      flexDirection: 'column',
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      position: 'relative',
      '&::before': {
        content: '""',
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        background: 'rgba(255, 255, 255, 0.1)',
        backdropFilter: 'blur(10px)',
      }
    }}>
      {/* Header */}
      <Box
        sx={{
          py: isMobile ? 1.5 : 2,
          px: isMobile ? 2 : 3,
          backgroundColor: 'rgba(255, 255, 255, 0.95)',
          backdropFilter: 'blur(20px)',
          borderBottom: '1px solid rgba(255, 255, 255, 0.2)',
          boxShadow: '0 4px 20px rgba(0, 0, 0, 0.1)',
          position: 'relative',
          zIndex: 10
        }}
      >
        <Container maxWidth="lg">
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
            <IconButton
              onClick={handleBackToPublic}
              sx={{
                backgroundColor: 'rgba(66, 133, 244, 0.1)',
                borderRadius: '12px',
                color: '#4285f4',
                '&:hover': {
                  backgroundColor: 'rgba(66, 133, 244, 0.2)',
                },
              }}
              size="small"
            >
              <ArrowBackIcon />
            </IconButton>
            <Typography
              variant="h6"
              sx={{
                fontWeight: 700,
                color: '#1a1a1a',
                letterSpacing: '0.5px'
              }}
            >
              EduConnect
            </Typography>
          </Box>
        </Container>
      </Box>

      {/* Main Content */}
      <Box
        component="main"
        sx={{
          flexGrow: 1,
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          py: isMobile ? 3 : 4,
          px: 2,
          position: 'relative',
          zIndex: 5
        }}
      >
        <Container maxWidth="sm">
          <Paper
            elevation={0}
            sx={{
              p: isMobile ? 3 : 5,
              borderRadius: '24px',
              backgroundColor: 'rgba(255, 255, 255, 0.95)',
              backdropFilter: 'blur(20px)',
              border: '1px solid rgba(255, 255, 255, 0.2)',
              boxShadow: '0 20px 40px rgba(0, 0, 0, 0.1)',
              position: 'relative',
              overflow: 'hidden',
              '&::before': {
                content: '""',
                position: 'absolute',
                top: 0,
                left: 0,
                right: 0,
                height: '4px',
                background: 'linear-gradient(90deg, #4285f4, #34a853, #fbbc05, #ea4335)',
              }
            }}
          >
            {/* Login Header */}
            <Box sx={{ textAlign: 'center', mb: isMobile ? 3 : 4, pt: 1 }}>
              <Box
                sx={{
                  display: 'inline-flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  width: isMobile ? 56 : 72,
                  height: isMobile ? 56 : 72,
                  borderRadius: '20px',
                  background: 'linear-gradient(135deg, #4285f4 0%, #34a853 100%)',
                  color: 'white',
                  mb: 2,
                  boxShadow: '0 8px 24px rgba(66, 133, 244, 0.3)',
                  position: 'relative',
                  '&::after': {
                    content: '""',
                    position: 'absolute',
                    inset: '2px',
                    borderRadius: '18px',
                    background: 'linear-gradient(135deg, rgba(255,255,255,0.2) 0%, rgba(255,255,255,0.05) 100%)',
                    pointerEvents: 'none',
                  }
                }}
              >
                <AdminIcon fontSize={isMobile ? "medium" : "large"} />
              </Box>
              <Typography
                variant={isMobile ? 'h5' : 'h4'}
                component="h1"
                gutterBottom
                sx={{
                  fontWeight: 700,
                  color: '#1a1a1a',
                  mb: 1
                }}
              >
                Panel de Administraci칩n
              </Typography>
              <Typography
                variant="body1"
                sx={{
                  maxWidth: '400px',
                  mx: 'auto',
                  color: '#666666',
                  fontSize: isMobile ? '0.875rem' : '1rem',
                  lineHeight: 1.5
                }}
              >
                Ingresa tus credenciales para acceder al sistema de gesti칩n de estudiantes
              </Typography>
            </Box>

            {/* Login Form */}
            <form onSubmit={handleSubmit}>
              <Box sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
                {authError && (
                  <Alert severity="error">
                    {authError}
                  </Alert>
                )}

                <TextField
                  label="Correo Electr칩nico"
                  type="email"
                  value={credentials.email}
                  onChange={handleInputChange('email')}
                  error={!!errors.email}
                  helperText={errors.email}
                  fullWidth
                  required
                  autoComplete="email"
                  disabled={isLoading}
                  variant="outlined"
                />

                <TextField
                  label="Contrase침a"
                  type="password"
                  value={credentials.password}
                  onChange={handleInputChange('password')}
                  error={!!errors.password}
                  helperText={errors.password}
                  fullWidth
                  required
                  autoComplete="current-password"
                  disabled={isLoading}
                  variant="outlined"
                />

                <Box sx={{ display: 'flex', gap: 2, mt: 2 }}>
                  <Button 
                    onClick={handleBackToPublic} 
                    disabled={isLoading}
                    color="inherit"
                    variant="outlined"
                    fullWidth
                  >
                    Volver
                  </Button>
                  <Button 
                    type="submit" 
                    variant="contained" 
                    disabled={isLoading}
                    fullWidth
                    sx={{ minHeight: 48 }}
                  >
                    {isLoading ? 'Iniciando sesi칩n...' : 'Iniciar Sesi칩n'}
                  </Button>
                </Box>
              </Box>
            </form>
          </Paper>
        </Container>
      </Box>

      {/* Footer */}
      <Box 
        component="footer" 
        sx={{ 
          py: 2, 
          px: 2, 
          mt: 'auto',
          backgroundColor: 'white',
          borderTop: `1px solid ${theme.palette.divider}`,
          textAlign: 'center'
        }}
      >
        <Typography variant="body2" color="text.secondary">
          춸 2024 EduConnect - Sistema de Gesti칩n de Estudiantes
        </Typography>
      </Box>
    </Box>
  );
};

export default LoginPage;
