import React, { useState, useEffect } from 'react';
import {
  Box,
  Card,
  CardContent,
  Typography,
  Grid,
  Chip,
  Paper,
  Divider,
  useTheme,
  useMediaQuery,
} from '@mui/material';
import {
  Person as PersonIcon,
  LocationOn as LocationIcon,
} from '@mui/icons-material';
import { studentApi, PublicStudent, ApiError } from '../services/api';
import LoadingSpinner from './LoadingSpinner';
import ErrorMessage from './ErrorMessage';
import { useNotification } from '../contexts/NotificationContext';

interface StudentListProps {
  refreshTrigger?: number; // Used to trigger refresh after new registration
}

const StudentList: React.FC<StudentListProps> = ({ refreshTrigger = 0 }) => {
  const [students, setStudents] = useState<PublicStudent[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [retryCount, setRetryCount] = useState(0);

  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));
  const { showError, showSuccess } = useNotification();

  const fetchStudents = async (showLoadingSpinner = true) => {
    try {
      if (showLoadingSpinner) {
        setLoading(true);
      }
      setError(null);
      const data = await studentApi.getPublicStudents();
      setStudents(data);

      // Show success message only on retry
      if (retryCount > 0) {
        showSuccess('Lista de estudiantes actualizada correctamente');
        setRetryCount(0);
      }
    } catch (err) {
      const apiError = err as ApiError;
      const errorMessage = apiError.message || 'Error al cargar la lista de estudiantes';
      setError(errorMessage);

      // Show notification for network errors
      if (apiError.status === 0 || errorMessage.toLowerCase().includes('conexión')) {
        showError(
          errorMessage,
          'Error de Conexión',
          {
            label: 'Reintentar',
            onClick: handleRetry
          }
        );
      }
    } finally {
      if (showLoadingSpinner) {
        setLoading(false);
      }
    }
  };

  useEffect(() => {
    fetchStudents();
  }, [refreshTrigger]);

  const handleRetry = () => {
    setRetryCount(prev => prev + 1);
    fetchStudents();
  };

  if (loading) {
    return (
      <LoadingSpinner 
        message="Cargando lista de estudiantes..." 
        size={50}
        timeout={8000}
        onTimeout={() => {
          showError(
            'La carga está tardando más de lo esperado. Verifica tu conexión a internet.',
            'Carga Lenta'
          );
        }}
      />
    );
  }

  if (error) {
    return (
      <ErrorMessage
        message={error}
        title="Error al cargar estudiantes"
        onRetry={handleRetry}
        retryText="Recargar lista"
        showRecoveryOptions={true}
        onGoHome={() => window.location.reload()}
        errorCode={retryCount > 0 ? `RETRY_${retryCount}` : undefined}
      />
    );
  }

  if (students.length === 0) {
    return (
      <Paper 
        elevation={1} 
        sx={{ 
          p: 4, 
          textAlign: 'center',
          backgroundColor: theme.palette.grey[50]
        }}
      >
        <PersonIcon 
          sx={{ 
            fontSize: 64, 
            color: theme.palette.grey[400],
            mb: 2 
          }} 
        />
        <Typography variant="h6" color="text.secondary" gutterBottom>
          No hay estudiantes registrados
        </Typography>
        <Typography variant="body2" color="text.secondary">
          Sé el primero en registrarte a un curso
        </Typography>
      </Paper>
    );
  }

  return (
    <Box>
      <Box sx={{ mb: 3, textAlign: 'center' }}>
        <Typography variant="h5" component="h2" gutterBottom>
          Estudiantes Registrados
        </Typography>
        <Typography variant="body2" color="text.secondary">
          {students.length} {students.length === 1 ? 'estudiante registrado' : 'estudiantes registrados'}
        </Typography>
        <Divider sx={{ mt: 2 }} />
      </Box>

      <Grid container spacing={isMobile ? 2 : 3}>
        {students.map((student) => (
          <Grid size={{ xs: 12, sm: 6, md: 4 }} key={student.id}>
            <Card 
              elevation={2}
              sx={{
                height: '100%',
                transition: 'all 0.3s ease-in-out',
                '&:hover': {
                  elevation: 4,
                  transform: 'translateY(-2px)',
                },
              }}
            >
              <CardContent sx={{ p: 3 }}>
                <Box 
                  display="flex" 
                  alignItems="center" 
                  gap={1} 
                  mb={2}
                >
                  <PersonIcon 
                    color="primary" 
                    sx={{ fontSize: 24 }} 
                  />
                  <Typography 
                    variant="h6" 
                    component="h3"
                    sx={{ 
                      fontWeight: 600,
                      lineHeight: 1.2,
                      wordBreak: 'break-word'
                    }}
                  >
                    {student.nombre} {student.apellido}
                  </Typography>
                </Box>

                <Box 
                  display="flex" 
                  alignItems="center" 
                  gap={1}
                  mt={1}
                >
                  <LocationIcon 
                    color="action" 
                    sx={{ fontSize: 20 }} 
                  />
                  <Chip
                    label={student.ciudad}
                    size="small"
                    variant="outlined"
                    color="primary"
                    sx={{
                      fontWeight: 500,
                      '& .MuiChip-label': {
                        px: 1.5
                      }
                    }}
                  />
                </Box>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      {/* Summary footer */}
      <Box 
        sx={{ 
          mt: 4, 
          p: 2, 
          backgroundColor: theme.palette.primary.main,
          color: 'white',
          borderRadius: 1,
          textAlign: 'center'
        }}
      >
        <Typography variant="body2">
          ¡Únete a nuestra comunidad de aprendizaje! 
          {students.length > 0 && ` Ya somos ${students.length} estudiantes.`}
        </Typography>
      </Box>
    </Box>
  );
};

export default StudentList;
