import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  Paper,
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Pagination,
  useTheme,
  useMediaQuery,
} from '@mui/material';
import {
  PersonAdd as PersonAddIcon,
} from '@mui/icons-material';
import { studentApi, PublicStudent, ApiError } from '../services/api';
import LoadingSpinner from './LoadingSpinner';
import ErrorMessage from './ErrorMessage';
import { useNotification } from '../contexts/NotificationContext';

interface StudentListProps {
  refreshTrigger?: number; // Used to trigger refresh after new registration
  onRegistrationClick: () => void;
}

const StudentList: React.FC<StudentListProps> = ({ refreshTrigger = 0, onRegistrationClick }) => {
  const [students, setStudents] = useState<PublicStudent[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [retryCount, setRetryCount] = useState(0);
  const [currentPage, setCurrentPage] = useState(1);
  const studentsPerPage = 5;

  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));
  const { showError, showSuccess } = useNotification();

  // Calculate pagination
  const totalPages = Math.ceil(students.length / studentsPerPage);
  const startIndex = (currentPage - 1) * studentsPerPage;
  const endIndex = startIndex + studentsPerPage;
  const currentStudents = students.slice(startIndex, endIndex);

  const fetchStudents = async (showLoadingSpinner = true) => {
    try {
      if (showLoadingSpinner) {
        setLoading(true);
      }
      setError(null);
      const data = await studentApi.getPublicStudents();
      setStudents(data);

      // Show success message only on retry
      if (retryCount > 0) {
        showSuccess('Lista de estudiantes actualizada correctamente');
        setRetryCount(0);
      }
    } catch (err) {
      const apiError = err as ApiError;
      const errorMessage = apiError.message || 'Error al cargar la lista de estudiantes';
      setError(errorMessage);

      // Show notification for network errors
      if (apiError.status === 0 || errorMessage.toLowerCase().includes('conexión')) {
        showError(
          errorMessage,
          'Error de Conexión',
          {
            label: 'Reintentar',
            onClick: handleRetry
          }
        );
      }
    } finally {
      if (showLoadingSpinner) {
        setLoading(false);
      }
    }
  };

  useEffect(() => {
    fetchStudents();
  }, [refreshTrigger]);

  const handleRetry = () => {
    setRetryCount(prev => prev + 1);
    fetchStudents();
  };

  if (loading) {
    return (
      <LoadingSpinner 
        message="Cargando lista de estudiantes..." 
        size={50}
        timeout={8000}
        onTimeout={() => {
          showError(
            'La carga está tardando más de lo esperado. Verifica tu conexión a internet.',
            'Carga Lenta'
          );
        }}
      />
    );
  }

  if (error) {
    return (
      <ErrorMessage
        message={error}
        title="Error al cargar estudiantes"
        onRetry={handleRetry}
        retryText="Recargar lista"
        showRecoveryOptions={true}
        onGoHome={() => window.location.reload()}
        errorCode={retryCount > 0 ? `RETRY_${retryCount}` : undefined}
      />
    );
  }

  const handlePageChange = (event: React.ChangeEvent<unknown>, value: number) => {
    setCurrentPage(value);
  };

  if (students.length === 0) {
    return (
      <Box>
        {/* Total Students Card */}
        <Paper
          elevation={1}
          sx={{
            p: 3,
            mb: 3,
            backgroundColor: '#ffffff',
            border: '1px solid #e0e0e0'
          }}
        >
          <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
            Total de Estudiantes
          </Typography>
          <Typography variant="h4" sx={{ fontWeight: 700, color: '#333333' }}>
            0
          </Typography>
        </Paper>

        {/* Registration Button */}
        <Button
          variant="contained"
          startIcon={<PersonAddIcon />}
          onClick={onRegistrationClick}
          sx={{
            backgroundColor: '#2196f3',
            color: 'white',
            textTransform: 'none',
            borderRadius: '20px',
            fontSize: '0.875rem',
            fontWeight: 500,
            px: 3,
            py: 1,
            mb: 3,
            '&:hover': {
              backgroundColor: '#1976d2',
            },
          }}
        >
          Registrarte como Estudiante
        </Button>

        {/* Empty State */}
        <Paper
          elevation={1}
          sx={{
            p: 4,
            textAlign: 'center',
            backgroundColor: '#ffffff',
            border: '1px solid #e0e0e0'
          }}
        >
          <Typography variant="h6" color="text.secondary" gutterBottom>
            No hay estudiantes registrados
          </Typography>
          <Typography variant="body2" color="text.secondary">
            Sé el primero en registrarte a un curso
          </Typography>
        </Paper>
      </Box>
    );
  }

  return (
    <Box>
      {/* Total Students Card */}
      <Paper
        elevation={1}
        sx={{
          p: 3,
          mb: 3,
          backgroundColor: '#ffffff',
          border: '1px solid #e0e0e0'
        }}
      >
        <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
          Total de Estudiantes
        </Typography>
        <Typography variant="h4" sx={{ fontWeight: 700, color: '#333333' }}>
          {students.length}
        </Typography>
      </Paper>

      {/* Registration Button */}
      <Button
        variant="contained"
        startIcon={<PersonAddIcon />}
        onClick={onRegistrationClick}
        sx={{
          backgroundColor: '#2196f3',
          color: 'white',
          textTransform: 'none',
          borderRadius: '20px',
          fontSize: '0.875rem',
          fontWeight: 500,
          px: 3,
          py: 1,
          mb: 3,
          '&:hover': {
            backgroundColor: '#1976d2',
          },
        }}
      >
        Registrarte como Estudiante
      </Button>

      {/* Students List Title */}
      <Typography variant="h6" sx={{ mb: 2, fontWeight: 600, color: '#333333' }}>
        Lista de Participantes ({students.length} estudiantes)
      </Typography>

      {/* Students Table */}
      <TableContainer
        component={Paper}
        elevation={1}
        sx={{
          mb: 3,
          backgroundColor: '#ffffff',
          border: '1px solid #e0e0e0'
        }}
      >
        <Table>
          <TableHead>
            <TableRow sx={{ backgroundColor: '#f5f5f5' }}>
              <TableCell sx={{ fontWeight: 600, color: '#333333' }}>
                Nombre
              </TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {currentStudents.map((student, index) => (
              <TableRow
                key={student.id}
                sx={{
                  '&:hover': { backgroundColor: '#f9f9f9' },
                  borderBottom: index === currentStudents.length - 1 ? 'none' : '1px solid #e0e0e0'
                }}
              >
                <TableCell sx={{ py: 2, color: '#333333' }}>
                  {student.nombre} {student.apellido}
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      {/* Pagination */}
      {totalPages > 1 && (
        <Box sx={{ display: 'flex', justifyContent: 'center', mb: 3 }}>
          <Pagination
            count={totalPages}
            page={currentPage}
            onChange={handlePageChange}
            color="primary"
            size="medium"
            sx={{
              '& .MuiPaginationItem-root': {
                color: '#666666',
                '&.Mui-selected': {
                  backgroundColor: '#2196f3',
                  color: 'white',
                  '&:hover': {
                    backgroundColor: '#1976d2',
                  },
                },
                '&:hover': {
                  backgroundColor: '#f0f0f0',
                },
              },
            }}
          />
        </Box>
      )}
    </Box>
  );
};

export default StudentList;
